import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 6,
  };

  render() {
    return (
      <React.Fragment>
        <span>{this.formatCount()}</span>
        <button>Increment</button>
      </React.Fragment>
    );
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? <h2>"Zero"</h2> : count;
  }
}

export default Counter;
